import React, { useState, useEffect } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import { Container } from "@mui/system";
import { Paper, Button } from "@mui/material";

export default function Product({ products }) {
  const paperStyle = {
    padding: "50 px 20 px",
    width: 600,
    margin: "20px auto",
  };

  const [name, setName] = React.useState("");
  const [category, setCategory] = React.useState("");
  const [stock, setStock] = React.useState("");

  const [data, setData] = useState({ data: [] });
  const [isLoading, setIsLoading] = useState(false);
  const [err, setErr] = useState("");

  const handleClick = (e) => {
    e.preventDefault();
    const product = { name, category, stock };
    console.log(product);
    fetch("http://localhost:8080/products/add", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(product),
    }).then(() => {
      console.log("New Product is added");
    });
  };

  const handleRefresh = async () => {
    setIsLoading(true);

    try {
      const response = await fetch("http://localhost:8080/products/getAll", {
        method: "GET",
        headers: {
          Accept: "application/json",
        },
      })
        .then((res) => res.json())
        .then((result) => {
          setData(result);
        });
    } catch (err) {
      setErr(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  console.log(data);

  return (
    <Container>
      <Paper elevation={3} style={paperStyle}>
        <h1>Add Product</h1>
        <Box
          component="form"
          sx={{
            "& > :not(style)": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <TextField
            id="outlined-basic"
            label="Product Name"
            variant="outlined"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <TextField
            id="outlined-basic"
            label="Category"
            variant="outlined"
            fullWidth
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          />

          <TextField
            id="outlined-basic"
            label="Stock"
            variant="outlined"
            fullWidth
            value={stock}
            onChange={(e) => setStock(e.target.value)}
          />
          <Button variant="contained" onClick={handleClick}>
            Add product to database
          </Button>
        </Box>
      </Paper>

      <Paper elevation={3} style={paperStyle}>
        <h1> Type in search bar to show products</h1>

        {products.map((product) => (
          <Paper
            elevation={6}
            style={{ margin: "10px", padding: "15px", textAlign: "left" }}
            key={product.id}
          >
            Id: {product.id} &nbsp; Name: {product.name} &nbsp; Category:{" "}
            {product.category} &nbsp; Stock: {product.stock}
          </Paper>
        ))}
        <br />
      </Paper>

      <Paper elevation={3} style={paperStyle}>
        <div>
          {err && <h2>{err}</h2>}

          <button onClick={handleRefresh}>Fetch data</button>

          {isLoading && <h2>Loading...</h2>}

          {data.data.map((prod) => {
            return (
              <div key={prod.id}>
                Id: {prod.id} &nbsp; Name: {prod.name} &nbsp; Category:{" "}
                {prod.category} &nbsp; Stock: {prod.stock}
                <br />
              </div>
            );
          })}
        </div>
      </Paper>
    </Container>
  );
}
